import {inject, NgModule} from "@angular/core";

import { Component } from '@angular/core';
import {FormControl, FormGroup, NgForm, Validators} from "@angular/forms";
import {Register} from "../../model/register";
import {Login} from "../../model/login";
import { JwtHelperService } from '@auth0/angular-jwt';
import {UserService} from "../../service/user.service";
import { take } from 'rxjs';

@Component({
  selector: 'app-auth',
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.css']
})
export class AuthComponent {
  userService = inject(UserService);

  isLoginMode = true;

  onSwitchMode() {
    this.isLoginMode = !isLo
  }

  loginForm!: FormGroup;
  registrationForm!: FormGroup;
  isLoggedIn: boolean = false;
  registrationSuccess: boolean = false;
  name = '';
  roles: string[] = [];
  ngOnInit(): void {
    if(localStorage.getItem('auth-token') !== '') {
      this.isLoggedIn = true;
      this.setName();
    }
    this.loginForm = new FormGroup({
      email: new FormControl('', [Validators.required]),
      password: new FormControl('', [Validators.required]),
    });

    this.registrationForm = new FormGroup({
      firstName: new FormControl('', [Validators.required]),
      lastName: new FormControl('', [Validators.required]),
      email: new FormControl('', [Validators.required]),
      password: new FormControl('', [Validators.required]),
      phone: new FormControl('', [Validators.required]),
      city: new FormControl('', [Validators.required]),
      address: new FormControl('', [Validators.required]),
    });
  }

  login() {
    this.userService
      .login(this.loginForm.value.username, this.loginForm.value.password)
      .pipe(take(1))
      .subscribe((response) => {
        localStorage.setItem(
          'auth-token',
          response.headers.get('auth-token') || ''
        );
        if (localStorage.getItem('auth-token') !== '') {
          this.isLoggedIn = true;
          this.setName();
        }
      });
  }

  register() {
    this.userService
      .register(
        this.registrationForm.value.firstName,
        this.registrationForm.value.lastName,
        this.registrationForm.value.email,
        this.registrationForm.value.password,
        this.registrationForm.value.phone,
        this.registrationForm.value.city,
        this.registrationForm.value.address,
      )
      .pipe(take(1))
      .subscribe((response) => {
        this.registrationSuccess = true;
      });
  }

  logout() {
    localStorage.setItem('auth-token', '');
    this.isLoggedIn = false;
  }
  onSwitchMode() {
    this.isLoginMode = !this.isLoginMode;
  }

  onSubmit(form: NgForm) {
    console.log(form.value);
    form.reset();
  }

  private setName() {
    const helper = new JwtHelperService();
    const decodedToken = helper.decodeToken(
      localStorage.getItem('auth-token') || ''
    );
    this.name = decodedToken?.sub;
    this.roles = decodedToken['roles'];
  }
}
