import {Injectable} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {Observable, of} from "rxjs";
import {User} from "../model/user";
import {environment} from "../environment";
import {Register} from "../model/register";
import {Login} from "../model/login";

@Injectable({
  providedIn: 'root'
})
export class UserService {
  private apiServerUrl = environment.apiBaseUrl;
  constructor(private http: HttpClient) { }

  register(user: Register): Observable<any> {
    return this.http.post<any>(`${this.apiServerUrl}/users/register`, user);
  }

  login(credentials: Login): Observable<any> {
    return this.http.post<any>(`${this.apiServerUrl}/users/login`, credentials);
  }
  getUsers(): Observable<User[]> {
     return this.http.get<User[]>(`${this.apiServerUrl}/users/findAllUsers`);
  }
  public getUserByEmail(email: string | null): Observable<User> {
    return this.http.get<User>(`${this.apiServerUrl}/users/findUserByEmail/${email}`);
  }
}
