import { EventEmitter, Component, Output } from '@angular/core';
import {UserService} from "../../service/user.service";
import {Router} from "@angular/router";
import {Login} from "../../model/login";
import {Register} from "../../model/register";

@Component({
  selector: 'app-login-form',
  templateUrl: './login-form.component.html',
  styleUrls: ['./login-form.component.css'],
})
export class LoginFormComponent {

  @Output() onSubmitLoginEvent = new EventEmitter();
  @Output() onSubmitRegisterEvent = new EventEmitter();

  constructor(private userService: UserService, private router: Router) {
  }

  active: string = "login";

  credentials: Login = {
    email: "",
    password: ""
  }

  register: Register = {
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    phone: "",
    city: "",
    address: ""
  }

  loginError = false;


  onLoginTab(): void {
    this.active = "login";
  }

  onRegisterTab(): void {
    this.active = "register";
  }

  login() {
    this.userService.login(this.credentials).subscribe(
      response => {
        const token = response.token;

        localStorage.setItem('token', token);

        this.router.navigate(['/items']);
      },
      error => {
        this.loginError = true;
        alert("Hibás e-mail vagy jelszó!");
      }
    )
  }

  onSubmitRegister(): void {
    this.onSubmitRegisterEvent.emit({
      "firstName": this.register.firstName,
      "lastName": this.register.lastName,
      "email": this.register.email,
      "password": this.register.password,
      "phone": this.register.phone,
      "city": this.register.city,
      "address": this.register.address
    });
  }

}
<div class="row justify-content-center">
  <div class="col-4">
    <ul class="nav nav-pills nav-justified mb-3" id="ex1" role="tablist">
      <li class="nav-item" role="presentation">
        <button [ngClass]="active == 'login' ? 'nav-link active' : 'nav-link'" id="tab-login"
                (click)="onLoginTab()" >Bejelentkezés</button>
      </li>
      <li class="nav-item" role="presentation">
        <button [ngClass]="active == 'register' ? 'nav-link active' : 'nav-link'" id="tab-register"
                (click)="onRegisterTab()" >Regisztráció</button>
      </li>
    </ul>

    <div class="tab-content">
      <div [ngClass]="active == 'login' ? 'tab-pane fade show active' : 'tab-pane fade'">
        <form (ngSubmit)="login()" #loginForm="ngForm">

          <div class="form-outline mb-4">
            <input type="text" id="loginName" name= "email" class="form-control" [(ngModel)]="credentials.email" />
            <label class="form-label" for="loginName">E-mail</label>
          </div>

          <div class="form-outline mb-4">
            <input type="password" id="loginPassword" name="password" class="form-control" [(ngModel)]="credentials.password" />
            <label class="form-label" for="loginPassword">Jelszó</label>
          </div>

          <button type="submit" class="btn btn-primary btn-block mb-4">Bejelentkezés</button>

        </form>
      </div>
      <div [ngClass]="active == 'register' ? 'tab-pane fade show active' : 'tab-pane fade'">
        <form (ngSubmit)="onSubmitRegister()" #registerForm="ngForm">

          <div class="form-outline mb-4">
            <input type="text" id="firstName" name="firstName" class="form-control" [(ngModel)]="register.firstName" />
            <label class="form-label" for="firstName">Vezetéknév</label>
          </div>

          <div class="form-outline mb-4">
            <input type="text" id="lastName" name="lastName" class="form-control" [(ngModel)]="register.lastName" />
            <label class="form-label" for="lastName">Keresztnév</label>
          </div>

          <div class="form-outline mb-4">
            <input type="text" id="login" name="login" class="form-control" [(ngModel)]="register.email" />
            <label class="form-label" for="login">E-mail</label>
          </div>

          <div class="form-outline mb-4">
            <input type="password" id="registerPassword" name="password" class="form-control" [(ngModel)]="register.password" />
            <label class="form-label" for="registerPassword">Jelszó</label>
          </div>

          <div class="form-outline mb-4">
            <input type="text" id="phone" name="phone" class="form-control" [(ngModel)]="register.phone" />
            <label class="form-label" for="phone">Telefonszám</label>
          </div>

          <div class="form-outline mb-4">
            <input type="text" id="city" name="city" class="form-control" [(ngModel)]="register.city" />
            <label class="form-label" for="city">Város</label>
          </div>

          <div class="form-outline mb-4">
            <input type="text" id="address" name="address" class="form-control" [(ngModel)]="register.address" />
            <label class="form-label" for="address">Utca, házszám</label>
          </div>

          <button type="submit" class="btn btn-primary btn-block mb-3">Regisztráció</button>
        </form>
      </div>
    </div>
  </div>
</div>
